import React from 'react';
import { connect } from 'react-redux';
import { NavLink, Link } from 'react-router-dom';
import styled from 'styled-components';

import * as actions from '../store/actions/actions';

const StylesNavBar = styled.header`
    height: 63px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: red;
    padding: 0 30px;

    .heading-secondary {
        text-decoration: none;
        color: #000;

        h2 {
            font-size: 4rem;
            margin: 0; 

        }
    }

    .nav-links {
        text-decoration: none;
        font-size: 2rem;
        font-weight: bold;
        padding: 20px;
        color: #000;
        margin-right: 30px;

        &:last-child {
            margin-right: 0;
        }

    }

    .active {
        display: inline-block;
        background-color: #000;
        color: #fff;
        padding: 20px;
    }
`;

const NavBar = props => {

    const logOutUserHandler = (event) => {
        event.preventDefault();
        localStorage.clear();
        props.onLoggingOut();
    }

    if (!!localStorage.getItem('userLogged') || props.loggedIn) {
        return (
            <StylesNavBar>
                <Link to="/home" className="heading-secondary"><h2>Tabless Thursday</h2></Link>
                <nav>
                    <NavLink exact to="/home" activeClassName="active" className="nav-links">Tabs</NavLink>
                    <NavLink exact to="/about" activeClassName="active" className="nav-links">About</NavLink>
                    <Link exact to="/" className="nav-links" onClick={logOutUserHandler}>Log Out</Link>
                </nav>
            </StylesNavBar>
        )
    } else {
        return (
            <StylesNavBar>
                <Link to="/" className="heading-secondary"><h2>Tabless Thursday</h2></Link>
                <nav>
                    <NavLink to="/login" activeClassName="active" className="nav-links">Sign in</NavLink>
                    <NavLink to="/register" activeClassName="active" className="nav-links">Sign Up</NavLink>
                </nav>
            </StylesNavBar>
        )
    }
}

const mapStateToProps = state => {
    return {
        loggedIn: state.loggedIn,
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onLoggingOut: () => dispatch(actions.logOutUser()),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
