import axios from 'axios';
import { axiosWithAuth } from '../../axiosAuth';

export const LOGIN_START = 'LOGIN_START';
export const LOGIN_SUCCESS = 'LOGIN_SUCCESS';
export const LOGIN_FAILURE = 'LOGIN_FAILURE';
export const LOGIN_END = 'LOGIN_END';

export const FETCH_START = 'FETCH_START';
export const FETCH_SUCCESS = 'FETCH_SUCCESS';
export const FETCH_FAILURE = 'FETCH_FAILURE';
export const FETCH_END = 'FETCH_END';


export function fetchData() {

}

const loginAPI = "https://tabless-thursday-backend.herokuapp.com/api/login";

export const login = ({ username, password, email }) => dispatch => {
    const credentials = { username, password, email };

    // dispatch({ type: LOGIN_START });

    axios
        .post(loginAPI, credentials)
        .then(response => {
            debugger 
            localStorage.setItem('token', response.data.token)
            dispatch({ type: LOGIN_SUCCESS, payload: response.data.tabs })
            let userID = response.data.user.id;
            axiosWithAuth()
                .get(`https://tabless-thursday-backend.herokuapp.com/api/users/${userID}`)
                .then(response => {
                    dispatch({ type: LOGIN_SUCCESS, payload: response.data.tabs })
                })
                .catch(error => {
                    debugger
                });
        })
        .catch(error => {
            debugger
        })
        .finally(() => {
            // dispatch({ type: LOGIN_END });
        })
}

export const getUserTabs = (id) => dispatch => {
    axiosWithAuth()
        .get(`https://tabless-thursday-backend.herokuapp.com/api/users/${id}`)
        .then(response => {
            debugger
        })
        .catch(error => {
            debugger
        });
}
