import React, { Component } from 'react';
import { connect } from 'react-redux';

import {Â registerUser } from '../store/actions/actions';

class SignUp extends Component {
    state = {
        username: '',
        password: '',
        email: ''
    }

    registerInputHandler = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    registerUserHadler = () => {
        const credentials = {
            username: this.state.username,
            password: this.state.password,
            email: this.state.email
        }

        this.props.onRegisteringUser(credentials);

        this.setState({
            username: '',
            password: '',
            email: ''
        })
    }

    render() {
        return (
            <form onSubmit={this.registerUserHadler}>
                <input 
                    type="text"
                    value={this.state.username}
                    name='username'
                    onChange={this.registerInputHandler}
                    placeholder='username'
                />
                <input 
                    type="password"
                    value={this.state.password}
                    name="password"
                    onChange={this.registerInputHandler}
                    placeholder="password"
                />
                <input 
                    type="email"
                    value={this.state.email}
                    name="email"
                    onChange={this.registerInputHandler}
                    placeholder="email"/>
                <button type="submit">Register</button>
            </form>
        );
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onRegisteringUser: (credentials) => dispatch({ type: registerUser, payload: credentials })
    }
}

export default connect(null, mapDispatchToProps)(SignUp);
