import axios from 'axios';

export const axiosWithAuth = () => {
    const token = localStorage.getItem('token');

    return axios.create({
        headers: {
            'Authorization': token,
        }
    })
}

export const axiosWithContent = () => {
    return axios.create({
        headers: {
            'Content-Type': 'application/json'
        }
    })
}
