import React from 'react';
import { connect } from 'react-redux';
import { NavLink, Link } from 'react-router-dom';
import styled from 'styled-components';

import * as actions from '../store/actions/actions';

const StylesNavBar = styled.header`
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: red;
    padding: 5px 30px;

    h2 {
        font-size: 2.5rem;
        margin: 0;
    }

    .nav-links {
        text-decoration: none;
        font-size: 1.2rem;
        font-weight: bold;

        &:first-child {
            margin-right: 30px;
        }

        & .active {
            background-color: #000;
            color: #fff;
        }
    }
`;

const NavBar = props => {

    const logOutUserHandler = () => {
        localStorage.clear();
        props.onLoggingOut();
    }

    if (!!localStorage.getItem('userLogged') || props.loggedIn) {
        return (
            <StylesNavBar>
                <Link to="/"><h2>Tabless Thursday</h2></Link>
                <nav>
                    <NavLink to="/tabs" >Tabs</NavLink>
                    <NavLink to="/about" >About</NavLink>
                    <NavLink to="/login" onClick={logOutUserHandler}>Log Out</NavLink>
                </nav>
            </StylesNavBar>
        )
    } else {
        return (
            <StylesNavBar>
                <h2>Tabless Thursday</h2>
                <nav>
                    <NavLink to="/login" activeClassName="active" className="nav-links">Sign in</NavLink>
                    <NavLink to="/register" className="nav-links">Sign Up</NavLink>
                </nav>
            </StylesNavBar>
        )
    }
}

const mapStateToProps = state => {
    return {
        loggedIn: state.loggedIn,
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onLoggingOut: () => dispatch({ type: actions.LOGOUT_USER }),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
