import React from 'react';
import { connect } from 'react-redux';

import { getUserTabs } from '../store/actions/actions';

class MainPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            userId: null,
            loggedIn: false,
        }
    }

    componentDidMount() {
        if(this.state.loggedIn === false) {
            this.setState({ userId: this.props.user.id, loggedIn: true })
            this.props.onGetUserTabs(this.state.userId)

        }

    }

    componentDidUpdate() {

        this.props.onGetUserTabs(this.props.user.id )
    }

    render() {
        return (
            <>
                Home page
            </>
        )
    }
}

const mapStateToProps = state => {
    return {
      user: state.user
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onGetUserTabs: (id) => dispatch(getUserTabs(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MainPage);
