import React, { Component } from 'react';
import { connect } from 'react-redux';

import {Â registerUser } from '../store/actions/actions';

class SignUp extends Component {
    state = {
        username: '',
        password: '',
        password2: '',
        email: ''
    }

    registerInputHandler = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    registerUserHandler = (event) => {
        event.preventDefault();

        if (this.state.password === this.state.password2) {
            const credentials = {
                username: this.state.username,
                password: this.state.password,
                email: this.state.email
            }

            this.props.registerUser(credentials).then(
                this.props.history.push('/login')
            );

            this.setState({
                username: '',
                password: '',
                password2: '',
                email: ''
            })
        } else {
            alert('passwords don\'t match');
            this.setState({
                password: '',
                password2: ''
            })
        }
    }

    render() {
        return (
            <form onSubmit={this.registerUserHandler}>
                <input 
                    type="text"
                    value={this.state.username}
                    name='username'
                    onChange={this.registerInputHandler}
                    placeholder='username'
                />
                <input 
                    type="password"
                    value={this.state.password}
                    name="password"
                    onChange={this.registerInputHandler}
                    placeholder="password"
                />
                <input 
                    type="password"
                    value={this.state.password2}
                    name="password2"
                    onChange={this.registerInputHandler}
                    placeholder="repeat password"
                />
                <input 
                    type="email"
                    value={this.state.email}
                    name="email"
                    onChange={this.registerInputHandler}
                    placeholder="email"/>
                <button type="submit">Register</button>
            </form>
        );
    }
}

