import * as types from '../actions/actions';

const initialState = {
    user: [],
    tabs: [],
    loggedIn: false,
    loading: false,
}

export function reducer(state = initialState, action) {
    switch(action.type) {
        case types.LOGIN_SUCCESS:
            return {...state, user: action.payload }
        default: 
            return state;
    }
}
